mapper.select:
SELECT * FROM blog
where blog_user=#{blog_user}
order by blog_time desc limit #{start}, #{size}

SELECT count(*) FROM blog
where blog_user=#{blog_user}
order by blog_time desc

mapper.update:
UPDATE blog SET 

	blog_title=#{blog_title},


	blog_content=#{blog_content}

WHERE blog_id=#{blog_id}
